def get_field_from_mongo(name):
    app_db = atp_classes.AppDB()

    return app_db.get_document_by_field('fields', 'name', name)


def update_fields_with_data_source(datasource, query_string):
    app_db = atp_classes.AppDB()

    return app_db.update_collection_by_query('fields', {'data_source': datasource},
                                             {'name': {"$in": query_string.split(',')}})


def iterate_and_update_data_source(datasource):
    if datasource == 'Experian Gold':
        update_fields_with_data_source('Experian Gold', EXPERIAN_GOLD)
    elif datasource == 'Experian Summarized Credit':
        update_fields_with_data_source('Experian Summarized Credit', EXPERIAN_SUMM_CREDIT)
    elif datasource == 'Experian Summarized Auto':
        update_fields_with_data_source('Experian Summarized Auto', EXPERIAN_SUMM_AUTO)
    elif datasource == 'Nielsen':
        update_fields_with_data_source('Nielsen', NIELSEN)
    elif datasource == 'MasterCard':
        update_fields_with_data_source('MasterCard', MASTER_CARD)
    elif datasource == 'MasterCard Custom Segments':
        update_fields_with_data_source('MasterCard Custom Segments', MASTER_CARD_CUS_SEG)
    elif datasource == 'Fandango':
        update_fields_with_data_source('Fandango', FANDANGO)
    elif datasource == 'Kantar Shopcom CPG':
        update_fields_with_data_source('Kantar Shopcom CPG', KANTAR_SHOPCOM_CPG)
    elif datasource == 'Polk':
        update_fields_with_data_source('Polk', POLK)
    elif datasource == 'Match Flag':
        update_fields_with_data_source('Match Flag', MATCH_FLAG)


